import React, { useEffect, useState } from "react";

function ResultsDisplay() {
  const [results, setResults] = useState([]); // Default state as empty array
  const [loading, setLoading] = useState(true); // Loading indicator
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    let isMounted = true; // To prevent updating state on unmounted component

    const fetchResults = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/results");
        if (!response.ok) {
          throw new Error(`Error fetching results: ${response.status}`);
        }

        const data = await response.json();
        if (isMounted) {
          setResults(data || []); // Ensure results is always an array
          setLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setError(err.message);
          setLoading(false);
        }
      }
    };

    fetchResults();

    // Cleanup function to prevent state update after unmount
    return () => {
      isMounted = false;
    };
  }, []);

  // Render loading state
  if (loading) {
    return <div>Loading crawl results...</div>;
  }

  // Render error state
  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="results-display">
      <h2>Crawl Results</h2>
      {results.length === 0 ? (
        <p>No results found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>URL</th>
              <th>Title</th>
              <th>Chatbots Detected</th>
              <th>Keywords Detected</th>
              <th>Date Collected</th>
            </tr>
          </thead>
          <tbody>
            {results.map((item, index) => (
              <tr key={index}>
                <td>
                  <a
                    href={item.main_url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {item.main_url}
                  </a>
                </td>
                <td>{item.title || "N/A"}</td>
                <td>
                  {item.detected_chatbots &&
                  item.detected_chatbots?.length > 0 ? (
                    <ul>
                      {item.detected_chatbots.map((chatbot, idx) => (
                        <li key={idx}>{chatbot}</li>
                      ))}
                    </ul>
                  ) : (
                    "None"
                  )}
                </td>
                <td>
                  {item.keywords_detected && item.keywords_detected.length > 0
                    ? item.keywords_detected.join(", ")
                    : "None"}
                </td>
                <td>{item.date_collected || "Unknown"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ResultsDisplay;
