import React, { useEffect, useState } from "react";

function ResultsDisplay() {
  const [results, setResults] = useState([]); // Default to an empty array
  const [loading, setLoading] = useState(true); // Track loading state
  const [error, setError] = useState(null); // Track any errors

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/results"); // Adjust the URL if necessary
        if (!response.ok) {
          throw new Error(`Error fetching results: ${response.status}`);
        }
        const data = await response.json();
        setResults(data || []); // Ensure results is an array, even if the data is empty or null
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchResults();
  }, []);

  if (loading) {
    return <div>Loading crawl results...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="results-display">
      <h2>Crawl Results</h2>
      {results.length === 0 ? (
        <p>No results found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>URL</th>
              <th>Title</th>
              <th>Chatbots Detected</th>
              <th>Keywords Detected</th>
              <th>Date Collected</th>
            </tr>
          </thead>
          <tbody>
            {results.map((item, index) => (
              <tr key={index}>
                <td>
                  <a
                    href={item.main_url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {item.main_url}
                  </a>
                </td>
                <td>{item.title || "N/A"}</td>
                <td>
                  {item.detected_chatbots &&
                  item.detected_chatbots.length > 0 ? (
                    <ul>
                      {item.detected_chatbots.map((chatbot, idx) => (
                        <li key={idx}>{chatbot}</li>
                      ))}
                    </ul>
                  ) : (
                    "None"
                  )}
                </td>
                <td>
                  {item.keywords_detected && item.keywords_detected.length > 0
                    ? item.keywords_detected.join(", ")
                    : "None"}
                </td>
                <td>{item.date_collected || "Unknown"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ResultsDisplay;
